const fetchWeatherData = (dataType) => {
    const url = new URL(BASE_URL + "/" + dataType);
    url.search = new URLSearchParams({appid: API_KEY });
    return fetch(url).then((res) => res.json());
};

const fetchFormattedData = async() => {
    const fetchCurrentWeather = await fetchWeatherData('weather')
        return fetchCurrentWeather;
}

export default fetchFormattedData;
