import { useContext, useState } from "react";
import { apiContext } from "../contexts/apiContext";

import {IoMdSearch} from 'react-icons/io';
import {ImSpinner8} from 'react-icons/im';

const SearchDisplay = (props) => {

    const {setLocation} = useContext(apiContext);

    const handlerSubmit = (event) => {
        event.preventDefault();
        setLocation(event.target.value);
    }

    const handlerChange = (event) => {
      event.preventDefault();
    };

    return (
        <div className="flex flex-row justify-center my-5 w-full">
                <form 
                    onSubmit={(event) => {handlerSubmit(event)}} 
                    className='flex justify-between bg-black opacity-25 rounded-lg w-full' 
                >
                    <input
                        onChange={(event) => {handlerChange(event)}}
                        className='text-xl rounded-lg font-light p-4 w-full shadow-xl bg-transparent focus:outline-none h-full text-white'
                        name='location' type='text'
                        placeholder='Search for a city here' 
                     />
                    <button className="bg-white p-3 m-2 rounded-lg">
                        <IoMdSearch />
                    </button>
                </form>
        </div>
    )
}

export default SearchDisplay;
