const API_KEY = 'd5c54a92b47f0f9c49fa254decc1ac92';
// const API_KEY = 'EZLG9SEBP724E777RBTYUX8JT';
const BASE_URL= `https://api.openweathermap.org/data/2.5`;
// const BASE_URL= `https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${location}?key=${WEATHER_API}`;

const weatherApi = (infoType, searchParams) => {
    const url = new URL(BASE_URL + "/" + infoType);
    url.search = new URLSearchParams({ ...searchParams, appid: API_KEY });
    return fetch(url).then((res) => res.json());
};

const currentWeatherData = (data) => {
    const {
        coord: { lat, lon },
        main: { temp, feels_like, temp_min, temp_max, humidity },
        name,
        dt,
        sys: { country },
        weather,
        wind: { speed },
      } = data;
}

export default weatherApi;
