import { useContext, useState } from "react";

import {IoMdSearch} from 'react-icons/io';
import {ImSpinner8} from 'react-icons/im';

const SearchBar = (props) => {

    const handlerSubmit = (event) => {
        event.preventDefault();
    }

    const handlerChange = (event) => {
      event.preventDefault();
    };

    const handlerSearchLocation = (event) => {
        event.preventDefault();
      };

    return (
        <div className='flex flex-row justify-center w-full'>
            <form 
                onSubmit={(event) => {handlerSubmit(event)}} 
                className='flex justify-between bg-white-500 rounded-lg w-full' 
            >
                <button type='submit' className='p-4'>
                    <IoMdSearch size={20} />
                </button>
                <input
                    onChange={(event) => {handlerChange(event)}}
                    className='rounded-lg font-light w-full shadow-xl bg-transparent focus:outline-none h-full text-black'
                    name='location' type='text'
                    placeholder='Type a city name here' 
                />
                <button onClick={handlerSearchLocation} className='p-4 bg-purple-500 whitespace-nowrap text-white rounded-lg'>
                    Or search my location
                </button>
            </form>
        </div>
    )
}

export default SearchBar;
