import axios from "axios";

const API_KEY = 'bc386dcaed81938a0aca5b5e06d419de';
const BASE_URL= `https://api.openweathermap.org/data/2.5`;

const getUrl = async(dataType, searchParams) => {
    axios.get(BASE_URL + "/" + dataType, {
        params: {
            q: searchParams,
            appid: API_KEY 
        }
    })
    .then((response) => {
        console.log(response.data)
    })
    .catch((error) => {
        console.log(error.message)
    })
}

const fetchFormattedData = async() => {
    const fetchCurrentWeather = await getUrl('weather', 'singapore')
        return fetchCurrentWeather;
        console.log(fetchCurrentWeather)
}

export default fetchFormattedData;
