import { useState } from "react";

import {IoMdSearch} from 'react-icons/io';
import {ImSpinner8} from 'react-icons/im';

const SearchBar = (props) => {
    const [searchResults, setSearchResults] = useState("");

    const handleSubmit = (event) => {
        event.preventDefault();
        props.onSubmit(searchResults);
    }

    const handleChange = (event) => {
      event.preventDefault();
      setSearchResults(event.target.value);
    };

    const handleGeoSearch = (event) => {
        event.preventDefault();
    };

    return (
        <div className='flex flex-row justify-center w-full'>

                <button onClick={handleSubmit} className='p-4'>
                    <IoMdSearch size={20} />
                </button>
                <input
                    onChange={handleChange}
                    className='rounded-lg font-light w-full shadow-xl bg-transparent focus:outline-none h-full text-black'
                    name='location' type='text'
                    placeholder='Type a city name here' 
                />

            <button onClick={(event) => {handleGeoSearch(event)}} className='p-4 bg-purple-500 whitespace-nowrap text-white rounded-lg'>
                Or search my location
            </button>
        </div>
    )
}

export default SearchBar;
