import './App.css';
import axios from 'axios';
import { useState, useEffect } from 'react';

import {ImSpinner8} from 'react-icons/im';

import SearchBar from './components/SearchBar.js';
import CurrentWeather from './components/CurrentWeather';
import ForecastWeather from './components/ForecastWeather';
import weatherApi from './services/weatherApi';

function App() {

  const fetchWeather = async() => {
    const data = await weatherApi("weather", {q: "singapore"});
    console.log(data);
  };

  // fetchWeather();

  // // loading icon if no results matches input by user
  // if (!data) {
  //   return (
  //     <ImSpinner8 className='text-5xl animate-spin' />
  //   )
  // }

  return (
    <div className="App -inset-1 bg-gradient-to-r from-purple-400 to-cyan-600 p-10">
      <div className='mx-auto max-w-screen-md h-fit shadow-xl p-10 bg-black/20 rounded-xl'>
        <SearchBar />
        <CurrentWeather />
        <ForecastWeather title="Daily Forecast" />
        <ForecastWeather title="Weekly Forecast" />
      </div>
    </div>
  );
}

export default App;
