import './App.css';

import SearchBar from './components/SearchBar.js';
import CurrentDisplay from './components/CurrentDisplay';
import ForecastDisplay from './components/ForecastDisplay';

function App() {
  return (
    <div className='App'>
      <div className="bg-gradient-to-r from-blue-400 to-cyan-600 p-10">
        <div className='mx-auto max-w-screen-md h-fit'>
          <SearchBar/>
        </div>
        <div className='mx-auto max-w-screen-md h-fit shadow-xl p-10 bg-white mt-10'>
          <CurrentDisplay />
        </div>
        <div className='mx-auto max-w-screen-md h-fit shadow-xl p-10 bg-white mt-10'>
          <ForecastDisplay title="Current weather" />
          <ForecastDisplay title="Forecast weather" />
        </div>
    </div>
    </div>
  );
}

export default App;
