import './App.css';

import { useState } from 'react';

import SearchBar from './components/SearchBar.js';
import CurrentDisplay from './components/CurrentDisplay';
import WeeklyDisplay from './components/WeeklyDisplay';
import HourlyDisplay from './components/HourlyDisplay';
import fetchFormattedData from './services/weatherApi';

function App() {
  const [weather, setWeather] = useState(null);
  const [location, setLocation] = useState('Singapore');

  const fetchWeather = async() => {
    const data = await fetchFormattedData({q: location}); // location = search input on submit
    console.log(data);
    // setWeather(data);
    // console.log(weather);
  };

  useEffect(() => {
    if (location){
      fetchWeather()
    }
  }, [location])

  return (
    <div className='App'>
      <div className="bg-gradient-to-r from-blue-400 to-cyan-600 p-10">
        <div className='mx-auto max-w-screen-md h-fit'>
          <SearchBar/>
        </div>
        <div className='mx-auto max-w-screen-md h-fit shadow-xl p-10 bg-white bg-opacity-75 mt-10'>
          <CurrentDisplay />
        </div>
        <div className='mx-auto max-w-screen-md h-fit shadow-xl p-10 bg-white bg-opacity-75 mt-10'>
          <HourlyDisplay />
          <WeeklyDisplay />
        </div>
    </div>
    </div>
  );
}

export default App;
