import { useContext, useState } from "react";

import {IoMdSearch} from 'react-icons/io';
import {ImSpinner8} from 'react-icons/im';

const SearchBar = (props) => {

    const handlerSubmit = (event) => {
        event.preventDefault();
    }

    const handlerChange = (event) => {
      event.preventDefault();
    };

    return (
        <div className='flex flex-row justify-center w-full'>
            <form 
                onSubmit={(event) => {handlerSubmit(event)}} 
                className='flex justify-between bg-white opacity-50 rounded-lg w-full' 
            >
                <input
                    onChange={(event) => {handlerChange(event)}}
                    className='text-xl rounded-lg font-light p-4 w-full shadow-xl bg-transparent focus:outline-none h-full text-black'
                    name='location' type='text'
                    placeholder='Search for a city here' 
                />
                <button type='submit' className='p-4'>
                    <IoMdSearch size={20} />
                </button>
                <button className='p-4'>
                    Find my location
                </button>
            </form>
        </div>
    )
}

export default SearchBar;
