import './App.css';
import axios from 'axios';
import { useState, useEffect } from 'react';

import {ImSpinner8} from 'react-icons/im';

import SearchBar from './components/SearchBar.js';
import CurrentWeather from './components/CurrentWeather';
import ForecastWeather from './components/ForecastWeather';
import fetchFormattedData from './services/weatherApi';

function App() {

  const [weather, setWeather] = useState(null);
  const [location, setLocation] = useState('Bucharest');

  // const fetchWeather = async() => {
  //   const data = await fetchFormattedData({q: "singapore"});
  //   setWeather(data);
  //   console.log(data);
  // };

  // fetchWeather();

  useEffect(() => {
    // set loading to true
    setLoading(true);

    const API_KEY = '762564e4d97f34461fcfcc9af696767d';
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=${API_key}`;

    axios
      .get(url)
      .then((res) => {
        // set the data after 1500 ms
        setTimeout(() => {
          setData(res.data);
          // set loading to false
          setLoading(false);
        }, 1500);
      })
      .catch((err) => {
        setLoading(false);
        setErrorMsg(err);
      });
  }, [location]);

  // // loading icon if no results matches input by user
  // if (!data) {
  //   return (
  //     <ImSpinner8 className='text-5xl animate-spin' />
  //   )
  // }

  return (
    <div className="App -inset-1 bg-gradient-to-r from-purple-400 to-cyan-600 p-10">
      <div className='mx-auto max-w-screen-md h-fit shadow-xl p-10 bg-black/20 rounded-xl'>
        <SearchBar />
        <CurrentWeather />
        <ForecastWeather title="Current weather" weather={weather} />
        <ForecastWeather title="Forecast weather" />
      </div>
    </div>
  );
}

export default App;
