import './App.css';
import { useState, useEffect } from 'react';

import {ImSpinner8} from 'react-icons/im';

import SearchBar from './components/SearchBar.js';
import CurrentWeather from './components/CurrentWeather';
import ForecastWeather from './components/ForecastWeather';
import fetchFormattedData from './services/weatherApi';

function App() {

  const [weather, setWeather] = useState(null);
  const [location, setLocation] = useState('Singapore');

  const fetchWeather = async() => {
    const data = await fetchFormattedData({q: location}); // location = search input on submit
    console.log(data);
    // setWeather(data);
    // console.log(weather);
  };

  // useEffect(() => {
  //   if (location){
  //     fetchWeather()
  //   }
  // }, [location])

  const fetchSearchResults = (searchResults) => {
    setLocation(searchResults);
  }

  return (
    <div className='App'>
      <div className="-inset-1 bg-gradient-to-r from-blue-400 to-cyan-600 p-10">
        <div className='mx-auto max-w-screen-md h-fit shadow-xl p-10 bg-white-800'>
          <SearchBar/>
        </div>
        <div className='mx-auto max-w-screen-md h-fit shadow-xl p-10 bg-white-800 mt-10'>
          <CurrentWeather />
        </div>
        <div className='mx-auto max-w-screen-md h-fit shadow-xl p-10 bg-white-800 mt-10'>
          <ForecastWeather title="Current weather" />
          <ForecastWeather title="Forecast weather" />
        </div>
    </div>
    </div>
  );
}

export default App;
