import { useContext, useState } from "react";
import { apiContext } from "../contexts/apiContext";

import {IoMdSearch} from 'react-icons/io';
import {ImSpinner8} from 'react-icons/im';

const SearchBar = (props) => {

    const {fetchWeather} = useContext(apiContext);

    const handlerChange = (event) => {
      event.preventDefault();
      setLocation(event.target.value);
    };

    return (
        <div className="container mx-auto">
            <form onSubmit={fetchWeather} className='flex justify-between' >
                <input onChange={(event) => {handlerChange(event.target.value)}} name='location' type='text' placeholder='Search for a city here'></input>
                <button>
                    <IoMdSearch />
                </button>
            </form>
        </div>
    )
}

export default SearchBar;
