import { createContext, useState, useEffect } from "react";
import axios from 'axios';

// const WEATHER_API = 'd5c54a92b47f0f9c49fa254decc1ac92';
const WEATHER_API = 'EZLG9SEBP724E777RBTYUX8JT';

export const apiContext = createContext();
export const ApiProvider = ({children}) => {

  const [searchText, setSearchText] = useState("");
  const [location, setLocation] = useState([]);

    // const apiURL = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${process.env.REACT_APP_WEATHER_API}`;
    const apiURL = `https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${location}?key=${WEATHER_API}`;

    // useEffect(() => {
    //   axios.get(apiURL)
    //     .then((response) => {
    //       setData(response.data);
    //       console.log(response.data)
    //     })
    //     .catch((error) => {
    //       console.log(error.message);
    //     })
    // }, [location]); // runs api when location state changes

    const handleSubmit = (event) => {
      event.preventDefault();
      if (!searchText) {
        alert("Unable to fetch results, please enter a valid city name.");
        return;
      }
      axios.get(
        `https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${location}?key=${WEATHER_API}`
      )
        .then((response) => {
          console.log(response.data);
          setLocation(response.data.Search);
        })
        .catch((error) => {
          console.log(error);
        });
    };

    const handlerChange = (event) => {
      event.preventDefault();
      setLocation(event.target.value);
    };

    return (
        <apiContext.Provider value={[location, setLocation]}>
          {children}
        </apiContext.Provider>
    )
}

export default ApiProvider;
