import './App.css';
import axios from 'axios';
import { useState, useEffect } from 'react';

import {ImSpinner8} from 'react-icons/im';

import SearchBar from './components/SearchBar.js';
import CurrentWeather from './components/CurrentWeather';
import ForecastWeather from './components/ForecastWeather';
import fetchFormattedData from './services/weatherApi';

function App() {

  const [weather, setWeather] = useState(null);
  const [location, setLocation] = useState();
  const [data, setData] = useState(null);

  // const fetchWeather = async() => {
  //   const data = await fetchFormattedData({q: "singapore"});
  //   setWeather(data);
  //   console.log(data);
  // };

  // fetchWeather();

  useEffect(() => {
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=762564e4d97f34461fcfcc9af696767d`;
    axios
      .get(url)
      .then((response) => {
        setData(response.data);
        console.log(data);
      })
      .catch((error) => {
        console.log(error.message);
      });
  }, [location]);

  // // loading icon if no results matches input by user
  // if (!data) {
  //   return (
  //     <ImSpinner8 className='text-5xl animate-spin' />
  //   )
  // }

  const fetchSearchInput = (data) => {
    setLocation(data);
    console.log(`${location} this`)
  }

  return (
    <div className="App -inset-1 bg-gradient-to-r from-purple-400 to-cyan-600 p-10">
      <div className='mx-auto max-w-screen-md h-fit shadow-xl p-10 bg-black/20 rounded-xl'>
        <SearchBar onSubmit={fetchSearchInput}/>
        <CurrentWeather />
        <ForecastWeather title="Current weather" />
        <ForecastWeather title="Forecast weather" />
      </div>
    </div>
  );
}

export default App;
