import axios from "axios";

const API_KEY = 'd5c54a92b47f0f9c49fa254decc1ac92';
const BASE_URL= `https://api.openweathermap.org/data/2.5`;

const fetchWeatherData = async(dataType, searchParams) => {
    const url = new URL(BASE_URL + "/" + dataType);
        axios.get(url, {
            params: {
              q: searchParams,
              appid: API_KEY 
            }
          })
          .then(function (response) {
            console.log(response);
          })
}

console.log(fetchWeatherData('weather', 'singapore'))
